public class DestinationCoordinates {
	private int x;
	private int y;
	
	public DestinationCoordinates(int x, int y) {
		// TODO Auto-generated constructor stub
		this.setX(x);
		this.setY(y);
	}

	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}
	
	
}
